mapper.update:
UPDATE work
SET workState = #{workState}
WHERE workID = #{workID};

UPDATE issue
SET issueState = #{issueState}
WHERE issueID = #{issueID};

UPDATE work
SET workContent = #{workContent},
   workTitle = #{workTitle},
   startDate = #{startDate},
   finishDate = #{finishDate}
WHERE workID = #{workID};

UPDATE issue
SET issueContent = #{issueContent},
	issueTitle = #{issueTitle}
WHERE issueID = #{issueID};
